@startuml
class AttendanceState {
  +List<Object?> props
}

class AttendanceInitial

class AttendanceLoading

class AttendanceLoaded {
  +Map<DateTime, List<AttendanceRecord>> attendanceByDate
  +List<LateEmployee> lateEmployees
}

class AttendanceError {
  +String message
}

class AttendanceRecord {
  +String employeeId
  +String employeeName
  +DateTime date
  +TimeOfDay checkInTime
}

class LateEmployee {
  +String employeeId
  +String employeeName
  +DateTime date
  +TimeOfDay checkInTime
  +TimeOfDay standardTime
  +int minutesLate
}

AttendanceState <|-- AttendanceInitial
AttendanceState <|-- AttendanceLoading
AttendanceState <|-- AttendanceLoaded
AttendanceState <|-- AttendanceError
@enduml

@startuml
class AttendanceView {
  +Widget build(BuildContext context)
}

class AttendancePage {
  +Widget build(BuildContext context)
  -Widget _buildInitialView(BuildContext context)
  -Widget _buildAttendanceView(BuildContext context, AttendanceLoaded state)
  -Widget _buildAttendanceList(BuildContext context, Map<DateTime, List<AttendanceRecord>> attendanceByDate)
  -Widget _buildLateList(BuildContext context, List<LateEmployee> lateEmployees)
  -Widget _buildErrorView(BuildContext context, AttendanceError state)
}

AttendanceView --> AttendancePage : creates
AttendancePage --> AttendanceCubit : uses
AttendancePage --> AttendanceState : observes
@enduml
